package pratice;

public class ForLoopsPatterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a;
		for(a=0;a<=10;++a)
		{
			System.out.print("+");
		}
		
		int b;
		for(b=1;b<=9;++b)
		{
			System.out.print("-");
		}
		
		int c;
		for(c=1;c<=5;++c)
		{
			System.out.print("+-");
		}
		
		int d;
		for(d=1;d<=3;++d)
		{
		//	System.out.print("*+-");
		}
		
		int e;
		for(e=1;e<=3;++e)
		{
			System.out.print("*+-");
		}
		{
			System.out.print("*");
		}
		int f;
		for(f=1;f<=3;++f)
		{
			System.out.print("**++");
		}
		int g;
		for(g=1;g<=2;++g)
		{
			System.out.print("***+++---");
		}
		int i;
		for(i=1;i<=2;++i)
		{
			System.out.print("***+++---");
			
		}
		//Bulit in function 
		String j = new StringBuilder("***+++---").reverse().toString(); {
			System.out.println(j);
		}
		
		
		


	}

}
